import React from 'react';
import Images from '../exports/Images';

import '../CSS/slider.css';

const Slider = () => {
  return (
    <motion.div className="slider container">
      <motion.div
        className="slider"
        drag="x"
        dragConstraints={{ right: 0, left: -212 }}
      >
        {Images2.map((Image2) => (
          <motion.div className="item">
            <img src={Image2} alt="" />
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  );
};
export default Slider;
