import React from 'react';
import Images from '../exports/Images';
import Images2 from '../exports/Images2';
import '../styles/slider.css';
import { motion } from 'framer-motion';

const Slider1 = () => {
  return (
    <motion.div className="slider-container">
      <h1>Fotos</h1>
      <motion.div
        className="slider"
        drag="x"
        dragConstraints={{ right: 0, left: -2123 }}
      >
        {Images.map((Image) => (
          <motion.div className="item">
            <img src={Image} alt="" />
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  );
};

const Slider2 = () => {
  return (
    <motion.div className="slider-container">
      <h1>Fotos</h1>
      <motion.div
        className="slider"
        drag="x"
        dragConstraints={{ right: 0, left: -2123 }}
      >
        {Images.map((Image) => (
          <motion.div className="item">
            <img src={Image} alt="" />
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  );
};

export default [Slider1, Slider2];
